package com.example.examen1import com.google.gson.GsonBuilderimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject Api {    private const val url = "192.168.118.95:3000"    //private val url = "192.168.0.129:3000"    private const val httpUrl = "http://$url/"    const val wsUrl = "ws://$url"    private var gson = GsonBuilder()        .setLenient()        .create()//   val tokenInterceptor = TokenInterceptor()    val okHttpClient = OkHttpClient.Builder().build()    val retrofit = Retrofit.Builder()        .baseUrl(httpUrl)        .addConverterFactory(GsonConverterFactory.create(gson))        .client(okHttpClient)        .build()}